        Set<Integer> visited = new HashSet();

        for (int i=0;i<quiet.length;i++) sol[i] = i;

        for (int i=0;i<quiet.length;i++)
            if (poorerThanI[i].size() == 0) visitDfs(i, richerThanI ,sol, visited ,quiet);

        return sol;
