    public void visitDfs(int poor, List[] richerThanI, int[] sol, Set<Integer> visited, int[] quiet){
        int min = poor;
        for (int richer: (List<Integer>) richerThanI[poor]) {
            if (!visited.contains(richer)) visitDfs(richer, richerThanI ,sol, visited ,quiet);
            if (quiet[sol[richer]]<quiet[min]) min = sol[richer];
        }
        visited.add(poor);
        sol[poor] = min;
    }
}
