import java.util.*;
class Solution 
{

    public int[] loudAndRich(int[][] richer, int[] quiet) 
    {
        ArrayList<ArrayList<Integer>>adj=new ArrayList<>();
        int n=quiet.length;
        for(int i=0;i<n;i++)
        {
          adj.add(new ArrayList<>());
        }
        for(int i=0;i<richer.length;i++)
        {
          adj.get(richer[i][1]).add(richer[i][0]);
        }
        int ans[]=new int[n];
        Arrays.fill(ans,Integer.MAX_VALUE);
        for(int i=0;i<n;i++)
        {
            if(ans[i]==Integer.MAX_VALUE)
            {
             int vis[]=new int[n];
             Stack<Integer>stk=new Stack<Integer>();
             topo(i,adj,vis,stk);
             while(!stk.isEmpty())
             {
                 int node=stk.peek();
                 stk.pop();
                 if(ans[i]==Integer.MAX_VALUE)
                 {
                     ans[i]=node;
                 }
                 else if(quiet[node]<quiet[ans[i]])
                 {
                     ans[i]=node;
                 }
             }
            }
        }

        return ans;
    }
    private void topo(int node,ArrayList<ArrayList<Integer>>adj,int vis[],Stack<Integer>stk)
    {
        vis[node]=1;
        for(int i:adj.get(node))
        {
            if(vis[i]==0)
             {
                 topo(i,adj,vis,stk);
             }
        }
        stk.push(node);
    }
}
