public:
    int dfs(vector<int>adj[],vector<int>&quiet,int i,vector<int>&dp) {
        if(dp[i]!=-1)return dp[i];

        dp[i]=i;
        for(auto it:adj[i]){
            int idx=dfs(adj,quiet,it,dp);
            if(quiet[idx]<quiet[dp[i]]){
                dp[i]=idx;
            }
        }
        return dp[i];
    }

    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {
        int n=quiet.size();
        vector<int>dp(n,-1);
        vector<int>adj[n];

        for(auto it:richer)adj[it[1]].push_back(it[0]);

        for(int i=0;i<n;i++)
        {
            if(dp[i]==-1)
            {
                dp[i]=dfs(adj,quiet,i,dp);
            }
        }

        return dp;
    }
};
