class Solution {
    func loudAndRich(_ richer: [[Int]], _ quiet: [Int]) -> [Int] {

        let i = quiet
            .enumerated()
            .sorted { $0.element < $1.element }
            .map { $0.offset }

        let p = richer.reduce(into: Array(repeating: [Int](), count: i.count)) {
            $0[$1[1]].append($1[0])
        }

        var res: [Int?] = Array(repeating: nil, count: i.count)

        func more(_ i: Int) -> Int {
            if let r = res[i] {
                return r
            }
            else {
                var r = quiet[i]
                for x in p[i] {
                    r = min(r, more(x))
                }
                res[i] = r
                return r
            }
        }

        for i in res.indices where res[i] == nil {
            more(i)
        }

        return res.map { i[$0!] }
    }
}
