class Solution {
    int[] ans;
    public int[] loudAndRich(int[][] richer, int[] quiet) {
        List<List<Integer>> adj = createGraph(quiet.length, richer);
        ans = new int[quiet.length];
        Arrays.fill(ans, -1);

        for(int i=0;i<quiet.length;i++) {
            ans[i] = dfs(adj, i, quiet)[0];
        }

        return ans;
    }

    public int[] dfs(List<List<Integer>> adj, int start, int[] quiet) {
        if(ans[start]>-1) { 
            int[] qVal = {ans[start], quiet[ans[start]]};
            return qVal;
        }

        List<Integer> neighbors = adj.get(start);
        int[] qVal = {start, quiet[start]};

        for(int nei: neighbors) {
            int[] val = dfs(adj, nei, quiet);
            if(val[1]<qVal[1]) {
                qVal = val;
            }
        }

        ans[start] = qVal[0];

        return qVal;
