class Solution {
public:

    int bfs(vector<vector<pair<int,int>>>& adj,int u,vector<int>& quiet)
    {
        queue<pair<int,int>> q;
        vector<bool> visited(quiet.size(),false);

        q.push({u,quiet[u]});

        int currquiet= quiet[u];
        int ans= u;

        while(!q.empty())
        {
            auto p= q.front();
            q.pop();

            visited[p.first]= true;
            int val= p.second;

            if(val<=currquiet)
            {
                 currquiet=val;
                 ans= p.first;
            }

            for(auto el: adj[p.first])
            {
                if(!visited[el.first])
                    q.push({el.first,el.second});
            }
        }
        return ans;
    }

    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {
        int n= quiet.size();

        vector<vector<pair<int,int>>> adj(n);

        for(auto el: richer)
        {
            int u= el[1];
            int v= el[0];

            adj[u].push_back({v,quiet[v]});
        }

        vector<int> ans(n,-1);

        for(int i=0;i<n;i++)
        {
            int h= bfs(adj,i,quiet);
            ans[i]= h;
        }
        return ans;
    }
};

