const tilingHelper = (n, m, memo) => {
    if (n > m) {
        return tilingHelper(m, n, memo);
    }

    if (n == 0) {
        return 0;
    }

    if (n == 1) {
        return m;
    }

    if (memo[n][m] !== 0) 
        return memo[n][m];

    let ans = Number.MAX_VALUE;

    /**
     * imagine a square of width n at coordinates (i, j)
     * In a spiral manner, four rectangles surround this square
     * [i , j + w]
     * [m - j - w , i + w]
     * [n - i - w , m - j]
     * [j , n - i]
     * 
     * Take min over all (i, j) and square sizes
     */
    for (let w = 1; w <= n; w++) {
        for (let i = 0; i <= n - w; i++) {
            for (let j = 0; j <= m - w; j++) {
                ans = Math.min(
                    ans,
                    tilingHelper(i, j + w, memo) +
                    tilingHelper(m - j - w, i + w, memo) +
                    tilingHelper(m - j, n - i - w, memo) +
                    tilingHelper(n - i, j, memo) + 1,
                );
            }
        }
    }

    memo[n][m] = ans;
    return ans;
};

/**
 * @param {number} n
 * @param {number} m
 * @return {number}
 */
var tilingRectangle = function(n, m) {
    const memo = new Array(14);
    for (let i = 0; i < 14; i++) memo[i] = new Array(14).fill(0);

    return tilingHelper(n, m, memo);
};
