class Solution {
public:
    int tilingRectangle(int n, int m) {
        vector<vector<int>> mem(14, vector<int>(14, 0));
        return cover(n, m, mem);
    }

    int cover(int n, int m, vector<vector<int>>& mem) {
        // simple special cases
        if(n > m) swap(n, m);
        if(n == 0) return 0;
        if(n == m) return 1;
        if(n == 1) return m;

        // use memory
        if(mem[n][m] > 0) return mem[n][m];

        // 1 square (s) + 1 rectangle (r)
        // sssrrrrr
        // sssrrrrr
        // sssrrrrr
        int mi = 1 + cover(n, m - n, mem);

        // 2 squares (b,s) + 3 rectangles (x,y,z) (big square size: i, small square size: m-i)
        // zzzzzyy
        // bbbbxyy
        // bbbbsss
        // bbbbsss
        // bbbbsss        
        for(int i = n - 1; i > 0 && m - i <= i; --i)
            for(int j = 0; j <= m - i; ++j)
                mi = min(mi, 2 + cover(2*i - m, j, mem) + cover(n - i, i + j, mem) + cover(n - m + i, m - i - j, mem));

        // memorize it
        mem[n][m] = mi;
        return mi;
    }
};
```

