class Solution {
    private int m;
    private int n;
    private boolean[][] filled;
    private int res;

    public int tilingRectangle(int n, int m) {
        this.m = m;
        this.n = n;
        this.filled = new boolean[m][n];
        this.res = m * n;

        backtrack(0, 0, 0);
        return res;
    }

    private void backtrack(int r, int c, int currRes) {
        if (currRes >= res) return;

        // check if we reached end of the reactange
        if (r >= m) {
            res = currRes; // since we already compared both and know currRes is smaller
            return;
        }

        // check if we reached current col, go to next row
        if (c >= n) {
            backtrack(r + 1, 0, currRes);
            return;
        }

        // check if current cell is filled move to next cell
        if (filled[r][c]) {
            backtrack(r, c + 1, currRes);
            return;
        }

        // find all possible size of k and try to fill from r, c
        // max square size should be always min of (m-r) and (n-c);
        for (int size = Math.min(m - r, n - c); size > 0 && allCellsAvailable(r, c, size); size--) {
            mask(r, c, size, true);
            backtrack(r, c + 1, 1 + currRes);
            mask(r, c, size, false);
        }
    }

    private void mask(int r, int c, int size, boolean value) {
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < size; j++) {
                filled[r + i][c + j] = value;
            }
        }
    }

    private boolean allCellsAvailable(int r, int c, int size) {
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < size; j++) {
                if (filled[r + i][c + j]) return false;
            }
        }

        return true;
    }
}

