    int dfs(int n, int m, vector<vector<int>> &dp){
        if(n == 11 && m == 13){
            return 6;
        }
        if(n == 13 && m == 11){
            return 6;
        }
        if(dp[n][m] != -1){
            return dp[n][m];
        }
        if(n <= 0 || m <= 0){
            return 0;
        }
        if(n == m){
            return 1;
        }
        if(n == 1){
            return m;
        }
        if(m == 1){
            return n;
        }
        int res = min(n, m);
        int answer = INT_MAX;
        for(int i = 1; i <= res; i++){
            int a = 1 + dfs(n - i, i , dp) + dfs(n, m - i, dp);
            int b = 1 + dfs(n - i, m , dp) + dfs(i, m - i, dp);

            answer = min(answer, min(a, b));

        }
        dp[n][m] = answer;
        return answer;
    }
};
