```
class Solution {
    int n, m;
    boolean[][] covered;
    int res;
    public int tilingRectangle(int n, int m) {
        this.n = n;
        this.m = m;
        this.covered = new boolean[n][m];
        this.res = m * n;
        backtrack(0);
        return res;
    }
    public void backtrack(int count){
        if(count >= res) return;
        boolean find = false;
        for(int r = 0; r < n; r++){
            for(int c = 0; c < m; c++){
                if(!covered[r][c]){
                    find = true;
                    int len = findMaxWidth(r, c);
                    while(len > 0){
                        cover(r, c, len, true);
                        backtrack(count + 1);
                        cover(r, c, len, false);
                        len--;
                    }
                    break;
                } 
            }
            if(find) break;
        }
        if(!find) res = Math.min(res, count);
        return;
    }
    public void cover(int r, int c, int len, boolean flag){
        for(int i = r; i < r + len; i++){
            for(int j = c; j < c + len; j++){
                covered[i][j] = flag;
            }
        }
    }
    public int findMaxWidth(int r, int c){
        int len = Math.min(n - r, m - c);
        while(true){
            boolean find = false;
            for(int i = r; i < r + len; i++){
