var dp = [14][14]int{}

func tilingRectangle(n int, m int) int {    
    var dfs func(n, m int)int
    dfs = func(n, m int) int {
        if n > m {return dfs(m, n)} //n < m always

        if n == 0 {return 0}

        if n == 1 {return m}

        if n == m {return 1}

        if dp[n][m] > 0 {return dp[n][m]}

        ans := 1<<30

        for l := 1; l <= n; l++ {
            if tmp := 1+dfs(l, m-l)+dfs(n-l, m); tmp < ans {ans = tmp}//divide to 1 sqare + 2 recs

            if tmp := 1+dfs(l, n-l)+dfs(m-l, n); tmp < ans {ans = tmp}//divide to 1 sqare + 2 recs

			//the for loop below divides the input to 2 sqare + 3recs as example3(n=11, m=13)
			//for exmaple 3, l is sqare 5, l2 is sqare 7, dfs(n-l, m-l2) is sqare 6, dfs(n-l2, m-l) is rectangle 4*8, dfs(l2-(n-l), m-l-l2) is sqare 1
            for l2 := n-l+1; l2 < n && l2+l < m; l2++{
