class Solution {
    int ans=Integer.MAX_VALUE;
    public int tilingRectangle(int n, int m) {
        dfs(0,0,new boolean[n][m],0);
        return ans;
    }
    public void dfs(int r,int c,boolean[][] b,int count){
        int n=b.length,m=b[0].length;
        if (count>=ans) return;
        if (r>=n){
            ans=count;
            return;
        }
        if (c>=m){
            dfs(r+1,0,b,count);
            return;
        }
        if(b[r][c]){
            dfs(r,c+1,b,count);
            return;
        }
        for (int k=Math.min(n-r,m-c);k>=1 && avai(r,c,b,k);k--){
            cover(r,c,b,k);
            dfs(r,c+1,b,count+1);
            back(r,c,b,k);
        }
    }
    public boolean avai(int r,int c,boolean[][] b,int k){
        for (int i=0;i<k;i++){
            for (int j=0;j<k;j++){
                if (b[i+r][j+c]) return false;
            }
        }
        return true;
    }
    public void cover(int r,int c,boolean[][] b,int k){
        for (int i=0;i<k;i++){
            for (int j=0;j<k;j++){
                b[i+r][j+c]=true;
            }
        }
    }
    public void back(int r,int c,boolean[][] b,int k){
        for (int i=0;i<k;i++){
            for (int j=0;j<k;j++){
                b[i+r][j+c]=false;
            }
        }
    }
}
