class Solution {
    private static int best;
    public int tilingRectangle(int n, int m) {
        best=n*m;
        int[][] T = new int[n][m];
        solve(T, 0);
        return best;
    }

    private void solve(int[][] T, int knownSquares) {
        if(knownSquares > best) return;
        int[] X = find(T);
        if(X[0]==-1) {
            best=knownSquares;
            return;
        }
        int L=maxWidth(T, X[0], X[1]);
        apply(T, X[0], X[1], L, 1);
        while( L >= 1 ) {
            solve(T, knownSquares+1);
            for(int i=X[0];i<X[0]+L;i++) T[i][X[1]+L-1]=0;
            for(int i=X[1];i<X[1]+L;i++) T[X[0]+L-1][i]=0;
            L--;
        }

    }

    private int maxWidth(int[][] T, int x, int y) {
        int L=0;
        while(true){
            boolean flag = false;
            if(x+L >= T.length || y+L >= T[0].length) break;
            for(int i=x;i<=x+L;i++){
                if(T[i][y+L]==1){
                    flag=true;break;
                }
            }
            if(flag) break;
            for(int i=y;i<=y+L;i++){
                if(T[x+L][i]==1){
                    flag=true;break;
                }
            }
            if(flag) break;
            L++;
        }
        return L;
    }

    private void apply(int[][] T, int x, int y, int L, int val){
        for(int i=x;i<x+L;i++){
            for(int j=y;j<y+L;j++){
                T[i][j]=val;
            }
        }
    }

    private int[] find(int[][] T) {
        for(int i=0;i<T.length;i++){
            for(int j=0;j<T[i].length;j++){
                if(T[i][j]==0) return new int[]{i,j};
            }
        }
        return new int[] {-1,-1};
    }
}
