```
class Solution(object):
    def tilingRectangle(self, n, m):
        """
        :type n: int
        :type m: int
        :rtype: int
        """
        dp = [n for _ in range(m)]
        q = deque()
        q.append([dp[:], 0])
        while q:
            dp, nstep= q.popleft()
            # print dp, nstep
            if sum(dp) == 0:
                return nstep

            i = self.find_left_bottom(dp)
            for s in range(min(m,n),0,-1):
                new_dp = self.fill(dp, i, s)
                if not new_dp:
                    continue
                q.append([new_dp, nstep+1])

    def find_left_bottom(self, dp):
        h = 0
        index = -1
        for i in range(len(dp)):
            if dp[i] > h:
                index = i
                h = dp[i]
        return index

    def fill(self, dp, i, s):
        if len(dp) - i < s:
            return
        dp_copy = dp[:]
        for j in range(i, i+s):
            if dp_copy[j] < s:
                return False
            dp_copy[j] -= s
        return dp_copy
