class Solution {
private:
    int minV = 1e9 + 5;

    void dfs(vector<int> & arr, int v, int n, int m) {
        if (v > minV) return;
        int minH = n, idx = 0, cnt = 0;

        for (int i = 0; i < m; i++) {
            if (arr[i] < minH) {
                minH = arr[i];
                idx = i;
            }
        }

        if (minH == n) {
            minV = min(minV, v);
            return;
        }

        for (int i = idx; i < arr.size(); i++) {
            if (arr[i] != arr[idx] || n - arr[i] <= cnt) break;
            cnt++;
        }

        for (int l = cnt; l >= 1; l--) {
            for (int i = idx; i < idx + l; i++) arr[i] += l;
            dfs(arr, v + 1, n, m);
            for (int i = idx; i < idx + l; i++) arr[i] -= l;
        }
    }
public:
    int tilingRectangle(int n, int m) {
        vector<int> arr(m, 0);
        dfs(arr, 0, n, m);

        return minV;
    }
};
