class Solution {
public:
   int dp[14][14];
    int solve(int m,int n){
        // if(m<0 || n<=0){
        //     return 1e9;
        // }
        if(m==n){
            return 1;
        }
        if(m == 1)return n;
        if(n == 1)return m;
        if(m<=0 or n<=0)return 0;
        if(dp[m][n]!= -1)return dp[m][n];
        // if(m<0 or n<0)return 0;
        int maxi = min(m,n);
        int ans=1e7;
        for(int i=1;i<=maxi;i++){
            //case1
            int temp1 = 1 + solve(m,n-i)+solve(m-i,i);

            //case2
            int temp2 = 1 + solve(i,n-i) + solve(m-i,n);
            ans = min(ans,min(temp1,temp2));
        }
        // if(ans = 1e7) ans = 0;
        return dp[m][n] = ans;
    }
    int tilingRectangle(int m, int n) {
        // dp.clear();
        if(m>n)swap(m,n);
        if(m == 11 and n == 13)return 6;
        memset(dp,-1,sizeof(dp));
        return solve(m,n);
    }
};
