        // if(m<0 or n<0)return 0;
        int maxi = min(m,n);
        int ans=1e7;
        for(int i=1;i<=maxi;i++){
            //case1
            int temp1 = 1 + solve(m,n-i)+solve(m-i,i);

            //case2
            int temp2 = 1 + solve(i,n-i) + solve(m-i,n);
            ans = min(ans,min(temp1,temp2));
        }
        // if(ans = 1e7) ans = 0;
        return dp[m][n] = ans;
