class Solution {
 public:
  int tilingRectangle(const int n, const int m) {
    vector<vector<bool>> covered(n, vector<bool>(m, false));
    int ret = numeric_limits<int>::max();
    dfs(n, m, 0, 0, 0, covered, ret);
    return ret;
  }

 private:
  void dfs(const int rows,
           const int cols,
           const int r,
           const int c,
           const int count,
           vector<vector<bool>> &covered,
           int &result) {
    if (count >= result) {
      return;
    }

    if (r == rows) {
      result = count;
      return;
    }

    if (c == cols) {
      return dfs(rows, cols, r + 1, 0, count, covered, result);
    }

    if (covered[r][c]) {
      return dfs(rows, cols, r, c + 1, count, covered, result);
    }

    for (int side_length = min(rows - r, cols - c); side_length > 0; --side_length) {
      if (can_cover(covered, r, c, side_length)) {
        cover(covered, r, c, side_length);
        dfs(rows, cols, r, c + side_length, count + 1, covered, result);
        uncover(covered, r, c, side_length);
      }
    }
  }

  bool can_cover(const vector<vector<bool>> &covered, const int r, const int c, const int side_length) {
    for (int row = r; row < r + side_length; ++row) {
      for (int col = c; col < c + side_length; ++col) {
        if (covered[row][col]) {
          return false;
        }
      }
    }
    return true;
  }

  void cover(vector<vector<bool>> &covered, const int r, const int c, const int side_length) {
    do_cover(covered, r, c, side_length, true);
  }

  void uncover(vector<vector<bool>> &covered, const int r, const int c, const int side_length) {
    do_cover(covered, r, c, side_length, false);
  }

  void do_cover(vector<vector<bool>> &covered,
                const int r,
                const int c,
                const int side_length,
                const bool to_cover) {
    for (int row = r; row < r + side_length; ++row) {
      for (int col = c; col < c + side_length; ++col) {
        covered[row][col] = to_cover;
      }
    }
  }
};
