                        for(int n=j;n<j+a;n++) {
                            if(mat[m][n] == 1) {
                                move = true;
                                break;
                            }
                        }
                        if(move) {
                            break;
                        }
                    }
                    if(move) {
                        continue;
                    }
                    for(int m=i;m<i+a;m++){
                        for(int n=j;n<j+a;n++) {
                            mat[m][n] = 1;
                        }
                    }
                    min(mat, i, j, ans+1);
                    for(int m=i;m<i+a;m++){
                        for(int n=j;n<j+a;n++) {
                            mat[m][n] = 0;
                        }
                    } 
                }
        }
    }
