class Solution:
    # From example 3, we see that simple cut across the whole board does not work
    # To solve the problem CORRECTLY, we need to go back to backtracking
    # To do that, we can build the board from the bottom
    # Try inserting the larger to lower possible squares into the index with minimum height
    # Condition to stop: 
    # 1. Whole board is filled -> return the result
    # 2. current number of moves is larger than or equal to the optimized result, stop searching

    def dfs(self, height, moves):
        if height == [self.n]*self.m:
            self.result =  min(self.result, moves)
            return
        if moves == self.result:
            return
        min_i, min_h = 0, height[0]
        for i, h in enumerate(height):
            if h < min_h:
                min_i, min_h = i, h
        max_width = 1
        while min_i + max_width < self.m and height[min_i + max_width] == min_h:
            max_width += 1
        max_height = self.n - min_h
        max_sq = min(max_height, max_width)
        for i in range(max_sq, 0, -1):
            for j in range(min_i, min_i + i):
                height[j] += i
            self.dfs(height, moves+1)
            for j in range(min_i, min_i + i):
                height[j] -= i            

    def tilingRectangle(self, n: int, m: int) -> int:
        self.result = m*n
        self.m, self.n = m, n
        self.dfs([0]*m, 0)
        return self.result
