class Solution:
    def tilingRectangle(self, n: int, m: int) -> int:
        # So then the problem becomes how do we fill the rect with as 
        # little squares as possible while knowing what's left empty.
        # We use greedy dfs to fill the rect with the biggest possible
        # sqaure starting from the TOP LEFT corner, and represent the 
        # left empty area with left, top, right, bottom variables.

        def fill_rect(l, t, r, b):
            if l == t == r == b:
                return 1
            # If I ever encounter a L shaped empty area, never cut it into
            # A reverse T shaped empty area. This only turns it into more possible squares

            # NOTE!!!! This assumption will not work if the n, m is greater than 13
            # But technically if we can represent ladder shaped area so how, this method
            # should work too?
            if l > r:
                # L shape on the left
                if l > t:
                    side = t
                    if t > l - r:
                        t = b - t
                    elif t == l - r:
                        t = b
                    l -= side
                elif l < t:
                    t -= l
                    b -= l
                else:
                    side = l
                    l = r
                    t = b - t
                    b -= side

                return 1 + fill_rect(l, t, r, b)
            elif l < r:
                # L shape on the right
                if r > t:
                    side = t
                    if t > r - l:
                        t = b - t
                    elif t == r - l:
                        t = b
                    r -= side
                elif r < t:
                    t -= r
                    b -= r
                else:
                    side = r
                    r = l
                    t = b - t
                    b -= side

                return 1 + fill_rect(l, t, r, b)

            # l == r rect or square
            max_side = min(l, t)
            count = fill_rect(
                l if l < t else l - max_side,
                t if l > t else t - max_side,
                r if l < t else r - max_side,
                b if l > t else b - max_side,
            )

            # try different size square fills if rect
            for side in range(max_side - 1, 0, -1):
                c = fill_rect(
                    l - side,
                    t - side,
                    r,
                    b
                )

                count = min(count, c)

            return 1 + count

        return fill_rect(n, m, n, m)
