var res int
func tilingRectangle(n int, m int) int {
    res = math.MaxInt64
    visited := make([][]bool, n)
    for i := 0; i < n; i++ {
        visited[i] = make([]bool, m)
    }

    dfs(0, 0, n, m, 0, visited)
    return res
}

func dfs(r, c, n, m, count int, visited [][]bool) {
    // If current count is max to already obtained count
    if count >= res {
        return
    }

    if r >= n {     // Covered all rows
        res = count // If we had found max than this, we would return earlier, so this is min
        return
    }

    for c < m && visited[r][c] {
        c++
    }

    if c >= m  {    // Covered all columns in a row
        dfs(r+1, 0, n, m, count, visited)
        return
    }    

    validMaxSquareLen := 0
    // If might have covered some columns already, check how big we can visit now  
    for validMaxSquareLen < min(n-r, m-c) && visited[r][c+validMaxSquareLen] == false {
        validMaxSquareLen++
    }

    for k := validMaxSquareLen; k >= 1; k-- {
        update(r, c, k, visited, true)
        dfs(r, c+k, n, m, count+1, visited)
        update(r, c, k, visited, false)
    }
}

func update(r, c, k int, visited [][]bool, value bool) {
    for i := r; i < r+k; i++ {
        for j := c; j < c+k; j++ {
            visited[i][j] = value
        }
    }
}

func min(a, b int) int {
    if a < b {
        return a
    }
    return b
}
