import numpy as np
import copy

class Solution(object):
    def tilingRectangle(self, n, m):
        """
        :type n: int
        :type m: int
        :rtype: int
        """
        board = np.zeros((m, n))

        def find_corner(board):
            for i in range(0, m):
                for j in range(0, n):
                    if board[i,j] == 0:
                        return (i, j)
            return None

        def fill(x1, y1, x2, y2, board):
            new_board = copy.deepcopy(board)
            new_board[x1:x2+1,y1:y2+1] = 1

            return new_board

        def dfs(board):
            node = find_corner(board)
            if node == None:
                return 0

            x = node[0]
            y = node[1]
            queue = []
            while x < m and y < n:
                if board[x,y] == 1 or board[node[0],y] == 1 or board[x,node[1]]:
                    break
                queue.append((x, y))
                x += 1
                y += 1

            output = []
            for i in range(len(queue)-1, int(len(queue)/2)-1, -1):
                next_node = queue[i]
                new_board = fill(node[0], node[1], next_node[0], next_node[1], board)
                num = dfs(new_board)+1                
                output.append(num)

            out = min(output)
            return out

        return dfs(board)
