```
class Solution {
    public int tilingRectangle(int n, int m) {
        int[][] mat = new int[m][n];
        int[] res = new int[1];
        res[0] = m * n;
        dfs(mat, 0, res, m, n);
        return res[0];
    }

    public void dfs(int[][] mat, int size, int[] res, int m, int n)
    {
        if(size > res[0])return;
        int x = -1, y = -1;
        for(int i = 0; i < m; i++)
        {
            boolean found = false;
            for(int j = 0; j < n; j++)
            {
                if(mat[i][j] == 0)
                {
                    x = i;
                    y = j;
                    found = true;
                    break;
                }
            }

            if(found)break;
        }


        if(x==-1 && y==-1)
        {
            res[0] = Math.min(res[0], size);
            return;
        }

        int maxWidth = getMaxWidth(mat, x, y, m, n);
        for(int w = maxWidth; w >= 1; w--)
        {
            setmat(mat, x, y, w, 1);
            dfs(mat, size+1, res, m, n);
            setmat(mat, x, y, w, 0);
        }
    }

    public void setmat(int[][] mat, int i, int j, int w, int val)
    {
        for(int x = i; x < i + w; x++)
        {
            for(int y = j; y < j + w; y++)
            {
                mat[x][y] = val;
            }
        }
    }

    public int getMaxWidth(int[][] mat, int i, int j, int m, int n)
    {
        int len = 1;
        while(i+len < m && j+len < n)
        {
            boolean isValid = true;
            for(int w = 0; w <= len; w++)
            {
                if(mat[i+len][j+w]==1 || mat[i+w][j+len]==1)
                {
                    isValid = false;
                    break;
                }
            }

            if(!isValid)break;
            len++;
        }

        return len;
    }
}
