class Solution {
    fun tilingRectangle(n: Int, m: Int): Int {
        val memo = mutableMapOf<String, Int>()
        val inf = m * n
        fun getKey(skyline: Array<Int>) : String = skyline.joinToString(",")
        fun rec(skyline: Array<Int>): Int =
            memo.getOrPut(getKey(skyline), {
                val minHeight = skyline.reduce { a, b -> minOf(a, b) }
                if(minHeight == n) return 0
                val start = skyline.indexOf(minHeight)
                var result = inf
                for (end in start until m) {
                    val newTileLen = end - start + 1
                    if (skyline[end] > minHeight || minHeight + newTileLen > n) {
                        break
                    }
                    for (i in start..end) {
                        skyline[i] += newTileLen
                    }
                    result = minOf(result, rec(skyline) + 1)
                    for (i in start..end) {
                        skyline[i] -= newTileLen
                    }
                }
                result
            })
        return rec(Array(m) { 0 })
    }
}
