            for (int j = c; j < c+k; j++){
                if (rect[i][j]) return false;
            }
        }
        return true;
    }
    private void coverOrUncover(boolean[][] rect, int r, int c, int k, boolean cover) {
        for (int i = r; i < r+k; i++){
            for (int j = c; j < c+k; j++){
                rect[i][j] = cover;
            }
        }
    }
}
