class Solution {

    private static int m, n;

    // ONE SINGLE SQUARE THAT WE WILL PLACE
    static class Box {
        private int xmin; // LOWER LEFT POINT.X
        private int xmax; // LOWER LEFT POINT.Y
        private int ymin; // UPPER RIGHT POINT.X
        private int ymax; // UPPER RIGHT POINT.Y

        Box(int xmin, int xmax, int ymin, int ymax) {
            this.xmin = xmin;
            this.xmax = xmax;
            this.ymin = ymin;
            this.ymax = ymax;
        }

        // CHECK IF TWO BOXES OVERLAP
        private boolean overlap(Box box) {
            return inside(box.xmin, box.ymin) || inside(box.xmin, box.ymax) || inside(box.xmax, box.ymin)
                    || inside(box.xmax, box.ymax)
                    || (box.xmax == this.xmax && box.xmin == this.xmin
                            && ((box.ymin > this.ymin && box.ymin < this.ymax)
                                    || (this.ymin > box.ymin && this.ymin < box.ymax)))
                    || (box.ymax == this.ymax && box.ymin == this.ymin
                            && ((box.xmin > this.xmin && box.xmin < this.xmax)
                                    || (this.xmin > box.xmin && this.xmin < box.xmax)));
        }

        // CHECK IF ONE VERTEX OF ONE BOX STRICTLY LIE INSIDE ANOTHER
        private boolean inside(int x, int y) {
            return x > xmin && x < xmax && y > ymin && y < ymax;
        }
    }

    // CHECK IF BOX b OVERLAPS IN ANY OF THE BOXES
    private boolean overlap(List<Box> boxes, Box b) {
        for (Box box : boxes) {
            if (b.overlap(box) || box.overlap(b)) {
                return true;
            }
        }
        return false;
    }

    private int maxSize = Integer.MAX_VALUE;

    // BOXES = BOXES PLACED IN GRID SO FAR
    // AREA = AREA OF THE GRID COVERED SO FAR
    // I, J = CURRENT COORDINATE WHERE WE WILL PLACE BOX (LOWER-LEFT VERTEX)
    private int solve(List<Box> boxes, int area, int i, int j) {

        // NO OVERLAP + AREA EQUAL = SOLVED !
        if (area == m * n) {
            maxSize = Math.min(maxSize, boxes.size());
            return 0;
        }

        // CANNOT EXCEED LIMIT FROM GREEDY
        if (j == n || boxes.size() > maxSize) {
            return Integer.MAX_VALUE;
        }

        int ans = Integer.MAX_VALUE;
        boolean found = false;

        // TRY SQUARES WITH ALL POSSIBLE SIDES. PLEASE NOTE THAT WE HAVE TAKEN
        // SIDE = Math.min(m - i, n - j) WHICH IS MAX POSSIBLE SIDE FROM CURRENT
        // (i, j) (WHICH IS LOWER LEFT VERTEX OF SQUARE)
        for (int side = Math.min(m - i, n - j); side >= 1; side--) {

            // HERE WE ARE ITERATING IN REVERSE FASHION TO IMPROVE PRUNING PERFORMANCE
            Box box = new Box(i, i + side, j, j + side); // NEW BOX

            if (!overlap(boxes, box)) { // CHECK OVERLAP WITH EXISTING BOXES

                found = true;
                boxes.add(box); // ADD TO EXISTING BOXES

                int x1;
                if (i + side == m) {
                    // ADD AREA OF THE LAST BOX IN CURRENT LEVEL, MOVE TO NEXT LEVEL
                    x1 = solve(boxes, area + side * side, 0, j + 1);
                } else {
                    // ADD AREA OF THIS BOX AND MOVE AHEAD IN CURRENT LEVEL ITSELF
                    x1 = solve(boxes, area + side * side, i + side, j);
                }

                boxes.remove(boxes.size() - 1); // BACKTRACK
                x1 = x1 == Integer.MAX_VALUE ? x1 : 1 + x1;
                ans = Math.min(ans, x1); // UPDATE ANS
            }
        }

        if (!found) {
            // THIS MEANS EVERY BOX FROM CURRENT (i, j) WAS OVERLAPPING WITH SOME OTHER BOX
            if (i + 1 == m) {
                ans = solve(boxes, area, 0, j + 1); // MOVE TO NEXT LEVEL
            } else {
                ans = solve(boxes, area, i + 1, j); // MOVE AHEAD IN CURRENT LEVEL ITSELF
            }
        }
        return ans;
    }

    private int greedy(int n, int m) {
        int x = Math.max(m, n);
        int y = Math.min(m, n);
        int c = 0;
        while (x != 0) {
            // KEEP FITTING THE LARGEST POSSIBLE BOX IN A GREEDY FASHION
            if (x >= y) {
                x -= y;
            } else {
                y -= x;
            }
            c++;
        }
        return c;
    }

    public int tilingRectangle(int n, int m) {
        Solution.m = m;
        Solution.n = n;
        maxSize = greedy(n, m); // FIND MAX NO OF BOXES VIA GREEDY FOR PRUNING
        int ans = solve(new ArrayList<>(), 0, 0, 0);
        return ans == Integer.MAX_VALUE ? 0 : ans;
    }
}
