class Solution {
    public int tilingRectangle(int n, int m) {

        int dp[][] = new int[14][14];
        for(int i = 0 ; i <= m ; i++){
            for(int j = 0 ; j <= n; j++)
                dp[i][j] = -1 ;
        }
        return minTiles(m, n, dp) ;
    }

    public int minTiles(int x, int y, int dp[][]){

        if(x == 0 || y == 0)
          return 0;

        if(x == y)
          return 1 ; 

        if(dp[x][y] != -1)
          return dp[x][y] ; 

        int min = x < y ? x : y ;
        int ans = Integer.MAX_VALUE ;

        for(int i = 1 ; i <= min ; i++){
            int firstRect =  minTiles(x - i, y, dp) ; 
            int secondRect = minTiles(i, y - i, dp) ;            
            int minimum1 = 1 + firstRect + secondRect  ;

            int firstRect2 = minTiles(x, y - i, dp) ;
            int secondRect2 = minTiles(x - i, i, dp) ;
            int minimum2 = 1 + firstRect2 + secondRect2 ;

            int mi = Math.min(minimum1, minimum2) ;
            ans = Math.min(ans, mi) ;
        }
        return dp[x][y] = dp[y][x] = ans ; 
    }
}

