class Solution {
public:
    int tilingRectangle(int n, int m) {
        memset(L, 0, sizeof(int) * 50625);
        memset(R, 0, sizeof(int) * 225);

        return dealShapeRect(n, m);
    }

protected:
    int dealShapeRect(int n, int m) {
        if (n == m) return 1;
        if (n > m) swap(n, m);

        int idx = n * 15 + m;

        if (R[idx] > 0) return R[idx];

        int i, minv = INT_MAX, v;

        for (i = 1; i < n; ++i) {
            v = dealShapeL(m - i, i, i, n - i);
            if (v < minv) minv  = v;
        }

        v = dealShapeRect(n, m - n);
        if (v < minv) minv = v;

        R[m*15+n] = R[idx] = minv + 1;

        return R[idx];
    }

    int dealShapeL(int l1, int l2, int l3, int l4) {
        int idx = ((l1 * 15 + l2) * 15 + l3) * 15 + l4;
        if (L[idx] > 0) return L[idx];

        //cout << l1 << "," << l2 << "," << l3 << "," << l4 << endl;
        int minv = INT_MAX;

        if (l1 == l2) minv = min(dealShapeRect(l3 + l1, l4), minv);
        if (l3 == l4) minv = min(dealShapeRect(l1, l2 + l4), minv);
        if (l2 > l1) {
            minv = min(dealShapeL(l1, l2 - l1, l3, l4), minv);
        } else if ((l1 > l2) && (l4 + l2 > l1)) {
            minv = min(dealShapeL(l4 + l2 - l1, l1, l1 - l2, l3), minv);
        }
        if (l3 > l4) {
            minv = min(dealShapeL(l1, l2, l3 - l4, l4), minv);
        } else if ((l4 > l3) && (l1 + l3 > l4)) {
            minv = min(dealShapeL(l1 + l3 - l4, l4, l4 - l3, l2), minv);
        }
        L[((l4 * 15 + l3) * 15 + l2) * 15 + l1] = L[idx] = minv + 1;

        return L[idx];
    }

private:
    int L[50625];
    int R[225];

};
