class Solution {
    int m, n;
    vector<vector<vector<vector<int>>>> dp;
public:
    int helper(int x1, int y1, int x2, int y2){
        if(x1 >= x2 || y1 >= y2) return 0;
        if(x1 > m || x2 > m || y1 > n || y2 > n) return 0;

        if(x2 - x1 == y2 - y1) return dp[x1][y1][x2][y2] = 1;
        if(dp[x1][y1][x2][y2] != 100000) return dp[x1][y1][x2][y2];

        int res = (x2 - x1) * (y2 - y1);
        for(int l = 1; l <= min(x2 - x1, y2 - y1); l++){
            for(int i = x1; i + l <= x2; i++){
                for(int j = y1; j + l <= y2; j++){
                    int one = helper(x1, y1, i + l, j) + helper(i + l, y1, x2, j + l) + helper(i, j + l, x2, y2) + helper(x1, j, i, y2);
                    int two = helper(x1, y1, x2, j) + helper(i + l, j, x2, j + l) + helper(x1, j + l, x2, y2) + helper(x1, j, i, j + l);
                    int three = helper(x1, y1, i, y2) + helper(i, y1, i + l, j) + helper(i, j + l, i + l, y2) + helper(i + l, y1, x2, y2);
                    res = min(res, 1 + min(one, min(two, three)));
                }
            }
        }
        return dp[x1][y1][x2][y2] = res;

    }
    int tilingRectangle(int m, int n) {
        this->m = m;
        this->n = n;
        dp.resize(m + 1, vector<vector<vector<int>>>(n + 1, vector<vector<int>>(m + 1, vector<int>(n + 1, 100000))));
        return helper(0, 0, m, n);
    }
};
