class Solution {
    bitset<170> rect;
    unordered_map<int, unordered_map<bitset<170>, int>> cache;
public:
    tuple<bool, int, int> can_fill(int n, int m, int x, int y, int size) {
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < size; j++) {
                if (rect[(m * (y + j)) + x + i] == 1) return {false, x+i, y+j};
            }
        }
        return {true, 0, 0};
    }

    void fill(int n, int m, int x, int y, int size, int bit) {
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < size; j++) {
                rect[(m * (y + j)) + x + i] = bit;
            }
        }
    }

    int search(int n, int m, int max_size) {
        if (cache.count(max_size) && cache[max_size].count(rect)) return cache[max_size][rect];

        int result = 0;

        for (int s = max_size; s > 1; s--) {
            for (int i = 0; i <= n - s; i++) {
                for (int j = 0; j <= m - s; j++) {
                    auto [can, next_i, next_j] = can_fill(n, m, i, j, s);
                    if (!can) {
                        i = next_i; j = next_j; continue;
                    }

                    fill(n, m, i, j, s, 1);

                    result = max(result, (s * s - 1) + search(n, m, s));

                    fill(n, m, i, j, s, 0);
                }
            }
        }

        return cache[max_size][rect] = result;
    }

    int tilingRectangle(int n, int m) {
        return n * m - search(n, m, min(n, m));
    }
};
