    while (pq.top() % 2 == 0) {
        res = min(res, pq.top() - min_n);
        min_n = min(min_n, pq.top() / 2);
        pq.push(pq.top() / 2);
        pq.pop();
    }
    return min(res, pq.top() - min_n);
}
