            min = tree.first();
            max = tree.last();
            result = Math.min(result, max-min);
        }

        return result;
    }

    private boolean finished(int min, int max, int result) {
        return result == 0 || isOdd(max);
    }

    private boolean isEven(int num) {
        return num % 2 == 0;
    }

    private boolean isOdd(int num) {
        return num % 2 != 0;
    }
}
