* ***Using Priority Queue***

* ***Time Complexity :- O(NlogN)***

* ***Space Complexity :- O(N)***

```
class Solution {
public:
    int minimumDeviation(vector<int>& arr) {

        int n = arr.size();

        // find maci and mini

        int maxi = INT_MIN;

        int mini = INT_MAX;

        for(int i = 0; i < n; i++)
        {
            if(arr[i] % 2 != 0)
            {
                arr[i] = 2 * arr[i];
            }

            maxi = max(maxi, arr[i]);

            mini = min(mini, arr[i]);
        }

        // declare a max heap

        priority_queue<int> pq;

        // push the elements into pq

        for(int i = 0; i < n; i++)
        {
            pq.push(arr[i]);
        }

        // find min_deviation

        int min_deviation = maxi - mini;

        int top = 0;

        while(pq.top() % 2 == 0)
        {
            top = pq.top();

            pq.pop();

            min_deviation = min(min_deviation, top - mini);

            mini = min(mini, top / 2);

            pq.push(top / 2);
        }

        top = pq.top();

