    int minimumDeviation(vector<int>& nums) {

        multiset<int> mset;

        //insert elements in multiset
        //here, we look to increase the min elements as much as possible
        //only the odd elemnts can be increased (multiply by 2) and that too only once (since it becomes even after that)
        //even elements are inserted as is and odd elements are inserted after multiplying by 2
        // if odd elements become greater than the actual max, after multiplication, don't worry, it will be reduced while decreasing the max element in the later loop. 

        for(auto n:nums){
            n % 2 == 0 ? mset.insert(n) : mset.insert(n << 1);
        }

        //get the max value
        int maxi = *mset.rbegin();

        //initialize ans to max - min value 
        int ans = maxi - *mset.begin();

        //Decrease the max element (if it is even) till the max can't be further reduced (till max is odd)
        while(maxi % 2 == 0){
            mset.erase(maxi);
            mset.insert(maxi / 2);
            maxi = *mset.rbegin();
            ans = min (ans, (maxi - *mset.begin()));
        }
        return ans;
    }
