class Solution {
public:
    int minimumDeviation(vector<int>& nums) 
    {
        //diamond notes 
        //maximise the odd elements by multiplying with 2 
        //and then make a priority queue and check the diff of maxh.top and min and then divide the element which is in top by 2 and then add it in heap and store difference 
        priority_queue<int>maxh;
