// An IntHeap is a min-heap of ints.
type IntHeap []int

func (h IntHeap) Len() int           { return len(h) }
func (h IntHeap) Less(i, j int) bool { return h[i] < h[j] }
func (h IntHeap) Swap(i, j int)      { h[i], h[j] = h[j], h[i] }

func (h *IntHeap) Push(x interface{}) {
	*h = append(*h, x.(int))
}

func (h *IntHeap) Pop() interface{} {
	old := *h
	n := len(old)
	x := old[n-1]
	*h = old[0 : n-1]
	return x
}

type MaxHeap struct {
	IntHeap
}

func (h MaxHeap) Less(i, j int) bool { return h.IntHeap[i] > h.IntHeap[j] }

func min(a, b int) int {
    if a < b {
        return a
    }

    return b
}

func minimumDeviation(nums []int) int {    
    minimum := math.MaxInt64
    evens := &MaxHeap{[]int{}}
    heap.Init(evens)

    for _, num := range nums {
        if num % 2 == 0 {
            heap.Push(evens, num)
            minimum = min(minimum, num)
        } else {
            heap.Push(evens, num * 2)
            minimum = min(minimum, num * 2)
        }
    }

    minDeviation := math.MaxInt64

    for evens.Len() > 0 {
        currentValue := heap.Pop(evens).(int)
        minDeviation = min(minDeviation, currentValue - minimum)

        if currentValue % 2 == 0 {
            heap.Push(evens, currentValue / 2)
            minimum = min(minimum, currentValue / 2)
        } else {
            break
        }
    }

    return minDeviation
}
```

