lass Solution {
public:
    int minimumDeviation(vector<int>& nums) {
        for(int& i : nums) {
            if(i & 1) {
                i *= 2;
            }
        }
        priority_queue<int> q;
        int small = *min_element(nums.begin(), nums.end());
        for(int i : nums)
            q.push(i);
        int ans = INT_MAX;
        while(1) {
            int top = q.top();
            q.pop();
            ans = min(ans, abs(top - small));
            if(top % 2 == 0) {
                top = top / 2;
                small = min(small, top);
                q.push(top);
            }
            else
                break;
        }
        return ans;
    }
};
```

