class Solution {
public:

    int minimumDeviation(vector<int>& nums) {
        set<int> s;
        for (int i : nums)
            s.insert(i % 2 ? i * 2 : i);
        int res = *s.rbegin() - *s.begin();
        while (*s.rbegin() % 2 == 0) {
            s.insert(*s.rbegin() / 2);
            s.erase(*s.rbegin());
            res = min(res, *s.rbegin() - *s.begin());
        }
        return res;
    }
};
