class Solution {
public:
    int minimumDeviation(vector<int>& nums) {
        int n= nums.size();
        int mn=INT_MAX;
        int mx = INT_MIN;

        priority_queue<int> pq;
        for(int i=0;i<n;i++){
            if(nums[i]%2!=0){
                nums[i] *=2;
            }
            mn = min(nums[i],mn);
            mx =max(nums[i],mx);
            pq.push(nums[i]);
        }
        int min_deviation = mx-mn;

        //hear in loop we are only trying to deal with max elements and trying to minimize them
         while((pq.top()) % 2 == 0)
        {
            int top = pq.top();
            pq.pop(); 
			// popped the top element

            min_deviation = min(min_deviation, top - mn);
            top /= 2;
            mn = min(mn, top);  
			// updating min

			pq.push(top);   
			// pushing again the top as we have to minimize the max
        }

        min_deviation = min(min_deviation,pq.top()-mn);

        return min_deviation;

    }
};
```

