int minimumDeviation(vector<int>& nums) 
{
	int result=2147483647,minimum=2147483647,maximum=-2147483648,n=nums.size();
	priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>> q;
	//sort from small to large by smallest number (eg. first element of each pair should be odd)
	for(int i=0;i<n;i++)
	{
		if(nums[i]%2==0)
		{
			int original=nums[i];
			while(nums[i]%2==0)
				nums[i]>>=1;
			q.push({nums[i],original}); //{13,52} if original nums[i]=52
		}
		else
			q.push({nums[i],nums[i]<<1}); //{13,26} if original nums[i]=13

		maximum=max(maximum,nums[i]);
		minimum=min(minimum,nums[i]);
	}
	result=maximum-minimum;
	while(!q.empty())
	{
		pair<int,int> p=q.top();
		q.pop();
		if(p.first==p.second) //the minimum can no longer be smaller
			return min(result,maximum-p.first);

		result=min(result,maximum-p.first); //update the minimum possible deviation
		p.first<<=1; //update the minimum number of the pair
		q.push(p);
		maximum=max(maximum,p.first); //update the maximum (because p.first is part of the current sets, so maximum should be updated as p.first being updated)
	}
	return -1; //will not reach this statement
}
