//Steps:
//1. Make all the odd numbers into even, by multiply 2
//2. Calcuate the devation
//3. Divide the largest number by 2 and than re-calcuate the devation
class Solution {
public:
    int minimumDeviation(vector<int>& nums) {
        int mini = INT_MAX;
        int maxi = INT_MIN;

        //1. Make all the odd numbers into even, by multiply 2
        for(int i = 0; i< nums.size(); i++){
            if(nums[i]%2!=0)
                nums[i] = nums[i] * 2;
            mini = min (mini, nums[i]);
            maxi = max (maxi, nums[i]);
        }

        int deviation = maxi - mini; 
        priority_queue<int> pq;

        for(auto x: nums)
            pq.push(x);

        while(pq.top()%2==0){
            int maxi = pq.top();
            pq.pop();
            deviation = min(deviation, maxi - mini);

            int newNum = maxi/2;
            pq.push(newNum);
            mini = min (mini, newNum);
        }

        deviation = min(deviation, pq.top() - mini);
        return deviation;
    }
};
```

