class Solution {
public:
    int minimumDeviation(vector<int>& nums) {
        //create a set st
        set<int> st;
        for(int i=0; i<nums.size(); i++)
        {
            // if the element is odd, multiply it by 2 and feed it in set
            if(nums[i] % 2!=0)
            nums[i] = nums[i] * 2;
            st.insert(nums[i]);
        }
// st.rbegin = max value, st.begin = min value
        int minDev = *st.rbegin() - *st.begin();
        // while max value in set is even
        while(*st.rbegin() % 2 == 0)
        {
            // remove the max value from set
            int temp = *st.rbegin();
            st.erase(temp);
            // divide it by 2
            temp = temp/2;
            // then feed it in set
            st.insert(temp);
            // put min deviation in minDev
            minDev = min(minDev, *st.rbegin() - *st.begin());
        }
        // return it
        return minDev;
    }
};

