/**
 * @param {number[]} nums
 * @return {number}
 */
class maxheap {
    constructor() {
        this.items = [];
    }

    add(x) {
        this.items.push(x);
        let len = this.items.length;
        let q = this.items;

        let c = len - 1;
        while(c>=0) {
            let s = null;
            let p = Math.floor((c-1) / 2);

            if(q[p] < q[c]) {
                s = true;
            }

            if(!s) break;
            let temp = q[p];
            q[p] = q[c];
            q[c] = temp;
            c= p;
        }
    }

    remove() {
        let q = this.items;
        if(q.length === 1) {
            return q.pop();
        }
        let res = q[0];
        q[0] = q[q.length - 1];
        q.pop();
        let len = q.length;
        let n = 0;
        while(true) {
            let l = 2*n + 1;
            let r = 2*n + 2;
            let s = null;

            if(l < len && q[l] > q[n]) {
                s = l;
            }
            if((s && q[r] > q[s]) || (!s && q[r] > q[n])) {
                s = r
            }

            if(!s) break;
            let temp = q[s];
            q[s] = q[n];
            q[n] = temp;
            n = s;
        }
        return res;
    }
}
var minimumDeviation = function(nums) {
    let set = new Set();
    const h = new maxheap();
    for(let no of nums) {
        if(no%2 === 0) {
            set.add(no);
            h.add(no)
        } else {
            set.add(no*2);
            h.add(no*2);
        }
    }
    let min = Math.min(...set);
    let diff = h.items[0] - min;

    while(set.size && h.items[0] %2 === 0) {
        let ele = h.remove();

        set.delete(ele);
        let nEle = parseInt(ele/2);
        if(!set.has(nEle)) {
            set.add(nEle);
            h.add(nEle);
        }
        min = Math.min(nEle, min);
        diff = Math.min(diff, h.items[0] - min)
    }
    return diff
};
```

