class Solution {
public:
    int minimumDeviation(vector<int>& nums) {

        int upper_odd = 1;
        int lower = 1000000000;
        for (const auto& num: nums) {
            upper_odd = max(upper_odd, num / (num & (-num)));
            lower = min(lower, num << (num & 1));
        }

        lower = min(lower, upper_odd);
        int lower2 = lower << 1;
        if (lower2 <= upper_odd) return upper_odd - lower;

        vector<int> arr({upper_odd});
        for (const auto& num: nums) {
            int a = num << 1;
            while (a >= lower2) a >>= 1;
