class Solution {
public:
    int minimumDeviation(vector<int>& nums) {
        set<int>st;
        for(auto num : nums) st.insert(num);

        int mnDev = *st.rbegin() - *st.begin();

        while(*st.begin() % 2 != 0){
            int x=*st.begin();
            st.erase(st.begin());
            st.insert(x*2);
            mnDev = min(mnDev, *st.rbegin() - *st.begin());
