    int getCount(int tar, vector<int>& nums){
        int lo = INT_MAX;
        int hi = INT_MIN;
        for(int i = 0;i<nums.size();i++){
            int cur = nums[i];
            if(cur%2==1){
                if(abs(tar-(cur*2))<abs(tar-(cur))){
                    cur*=2;
                }
            }
            else{
                while(cur%2==0&&abs(tar-(cur/2))<abs(tar-(cur))){
                    cur/=2;
                }
            }
            hi = max(hi,cur);
            lo = min(lo,cur);
        }
        return hi-lo;
    }
    int minimumDeviation(vector<int>& nums) {
        sort(nums.begin(),nums.end());
        int l = 0;
        int r = nums[nums.size()-1];
        int mid1 = l + (r-l)/3;
        int mid2 = r - (r-l)/3; 
        int mi = INT_MAX;
        while(l<=r){
            mid1 = l + (r-l)/3;
            mid2 = r - (r-l)/3; 
            int c1 = getCount(mid1,nums);
            int c2 = getCount(mid2,nums);
            mi = min({mi,c1,c2});
            if(c1<c2){
                r = mid2-1;
            }
            else{
                l = mid1+1;
            }
        }
        return mi;
    }
};
