class Solution {
public:
    int minimumDeviation(vector<int>& nums) {

        int N = nums.size();
        set<int> s;
        for(int i = 0; i < N; i++) {

            if (nums[i] & 1) {
                s.insert(nums[i] * 2);
            }

            else {
                s.insert(nums[i]);
            }
        }

        int cur_diff = *s.rbegin() - *s.begin();

        while (*s.rbegin() % 2 == 0)
        {
            int div_eve = *s.rbegin();
            s.erase(div_eve);
            div_eve = div_eve / 2;
            s.insert(div_eve);

            cur_diff = min(cur_diff, *s.rbegin() - *s.begin());
        }

        return cur_diff;

    }
};
```

